/*global kakao*/
import React, { useEffect } from "react";
import styled from "styled-components";

const MapContainer = styled.div`
  position: relative;
  width: 1080px;
  height: 1920px;
  margin-bottom: 50px;
`;

function Map() {
  useEffect(() => {
    const script = document.createElement("script");
    script.async = true;
    script.src =
      "http://dapi.kakao.com/v2/maps/sdk.js?appkey=a9cb6f1eaea0879aee7e0eaff3eb864b&autoload=false";
    document.head.appendChild(script);
    script.onload = () => {
      kakao.maps.load(() => {
        let container = document.getElementById("map"); // 지도를 표시할 div
        let options = {
          center: new kakao.maps.LatLng(37.61128226353329, 126.91724375313038),
          level: 10, // 지도의 확대 레벨
        };
        const map = new window.kakao.maps.Map(container, options); // 지도 생성
        console.log("loading kakaomap");

        // 마커가 표시될 위치
        let markerPosition = new kakao.maps.LatLng(
          37.61128226353329,
          126.91724375313038
        );

        // 마커 생성
        var marker = new kakao.maps.Marker({
          position: markerPosition,
        });

        marker.setMap(map);

        // 아래 코드는 지도 위의 마커를 제거하는 코드
        // marker.setMap(null);
      });
    };
  });

  return (
    <>
      <MapContainer id="map" />
    </>
  );
}

export default Map;
