/*global kakao*/
import React, { useEffect } from "react";
import styled from "styled-components";

const MapContainer = styled.div`
  position: relative;
  width: 100px;
  height: 100px;
  margin-bottom: 50px;
`;

const HR = styled.div`
  width: 100%
  height: 60px;
  background-color: red;
  font-size: 1px;
`;

function Map() {
  useEffect(() => {
    const script = document.createElement("script");
    script.async = true;
    script.src =
      "http://dapi.kakao.com/v2/maps/sdk.js?appkey=a9cb6f1eaea0879aee7e0eaff3eb864b&autoload=false";
    document.head.appendChild(script);
    script.onload = () => {
      kakao.maps.load(() => {
        let container = document.getElementById("map"); // 지도를 표시할 div
        let options = {
          center: new kakao.maps.LatLng(37.61128226353329, 126.91724375313038),
          level: 1, // 지도의 확대 레벨
        };
        const map = new window.kakao.maps.Map(container, options); // 지도 생성
        console.log("loading kakaomap");

        // 마커가 표시될 위치
        let markerPosition = new kakao.maps.LatLng(
          37.61128226353329,
          126.91724375313038
        );

        // 마커 생성
        let marker = new kakao.maps.Marker({
          position: markerPosition,
        });

        marker.setMap(map);

        // 아래 코드는 지도 위의 마커를 제거하는 코드
        // marker.setMap(null);

        var iwContent =
            '<div style="padding:5px;">구산역! <br><a href="https://map.kakao.com/link/map/Hello World!,33.450701,126.570667"</div>', // 인포윈도우에 표출될 내용으로 HTML 문자열이나 document element가 가능합니다
          iwPosition = new kakao.maps.LatLng(
            37.61128226353329,
            126.91724375313038
          ); //인포윈도우 표시 위치입니다

        // 인포윈도우를 생성합니다
        var infowindow = new kakao.maps.InfoWindow({
          position: iwPosition,
          content: iwContent,
        });

        // 마커 위에 인포윈도우를 표시합니다. 두번째 파라미터인 marker를 넣어주지 않으면 지도 위에 표시됩니다
        infowindow.open(map, marker);
      });
    };
  });

  return (
    <>
      <MapContainer id="map" />
      <HR> </HR>
      <hr></hr>
    </>
  );
}

export default Map;
